// import { set } from "mongoose";
import React, { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

function UpdateProducts() {
  const [prname, setPrname] = useState("");
  const [prprice, setPrprice] = useState(0);
  const [primg, setPrimg] = useState("");

  async function handleSubmit(){
    try {
      const response = await axios.post("/products/update", {
        productName: prname,
        price: prprice,
        img: primg
      });
      if (response.status === 401) {
        alert(response.data.status);
        window.location.href = "/login";
      }
      else {
        alert(response.data.status);
        setPrname("");
        setPrprice(0);
        setPrimg("");
      }
    } catch (error) {
      console.error("Error during signup", error);
    }
  }

  useEffect(() => {
    if (!document.cookie.includes("sessionId")) {
      alert("You won't be able to update products without logging in. Please login as admin.");
    }
  }, []);

  return (
    <div>
      <h2>Update Products</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Product name:
            <input
              type="text"
              value={prname}
              onChange={(e) => setPrname(e.target.value)}
            />
          </label>
        </div>
        <div>
          <label>
            Price:
            <input
              type="number"
              value={prprice}
              onChange={(e) => setPrprice(e.target.value)}
            />
          </label>
        </div>
        <button type="submit">Login</button>
        <div>
          <label>
            Image link:
            <input
              type="text"
              value={primg}
              onChange={(e) => setPrimg(e.target.value)}
            />
          </label>
        </div>
        <button type="submit">Add</button>
        <button type="submit">Update</button>
        <button type="submit">Delete</button>
      </form>
    </div>
  );
}

export default UpdateProducts;
